import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from "axios";
import './Home.css';
import Profile from './Profile';

function Home() {
  const [user, setUser] = useState({ id: Profile.getId(), username: Profile.getUsername() });
  const navigate = useNavigate();
  console.log(user.id);
  console.log(user.username);

  useEffect(() => {
    var idcheck = Profile.getId();
    if (idcheck === 'null') {
      Profile.clearProfile();
      navigate("/Login");
    }
  }, []);

  function handleClick() {
    Profile.clearProfile();
    navigate("/Login");
  }

  function handleSubmit(event) {
    event.preventDefault();
    const formData = new FormData(event.target);
    console.log(formData);
    const task = { taskname: formData.get("taskname"), completion: false }
    var bod = { "id": user.id, "task": task };

    axios.post(`http://localhost:8000/addtask`, bod)
      .then(res => {
        console.log(res.data);
      })
      .catch(function (error) { if (error.response) { console.log(error.response.data); } });
  }

  // var bod = { "id": user.id, "task": { taskname: null, completion: false } };
  // axios.post(`http://localhost:8000/deltask`, bod)
  //     .then(res => {
  //       console.log(res.data);
  //     })
  //     .catch(function (error) { if (error.response) { console.log(error.response.data); } });

  function deleteTask(){

  }


  return (
    <div className="Home">

      <div className="Home-header">
        <label className='username'>{user.username}</label>
        <button className="Button2" onClick={handleClick}>Sign out</button>
      </div>
      <br></br><br></br>
      <div className="addTask">
        <form method="post" onSubmit={handleSubmit}>
          <label className="taskname">
            Add task
          </label><br></br>
          <input className="input taskname" name="taskname"/>
          <button>Add</button>
        </form>
      </div>
      <form method="post" onSubmit={handleSubmit}>
          <label className="taskname">
            Delete task
          </label><br></br>
          <input className="input taskname" name="taskname"/>
          <button>Delete</button>
        </form>






    </div>


  )
};

export default Home;
