import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from "axios";
import './Home.css';
import Profile from './Profile';
import Task from './Task.js';

function Home() {
  const [user, setUser] = useState({ id: Profile.getId(), username: Profile.getUsername() });
  const [tasks, setTasks] = useState([]);
  const [rerender, setRerender] = useState(1);
  let rer=[1];
  const navigate = useNavigate();
  console.log(user.id);
  console.log(user.username);
  console.log(tasks);


  useEffect(() => {
    var idcheck = Profile.getId();
    if (idcheck === 'null' || idcheck === null) {
      Profile.clearProfile();
      navigate("/Login");
    }
    else {
      const fetchTasks = async () => {
        try {
          const result = await Profile.getTasks();
          setTasks(result);
        } catch (error) { console.error('Error fetching task list:', error); }
      };
      fetchTasks();
    }
  }, [rer]);

  function signout() {
    Profile.clearProfile();
    navigate("/Login");
  }

  function addTask(event) {
    event.preventDefault();
    const formData = new FormData(event.target);
    const task = { taskname: formData.get("taskname"), completion: false }
    var bod = { "id": user.id, "task": task };

    axios.post(`http://localhost:8000/addtask`, bod)
      .then(res => {
        console.log(res.data);
      })
      .catch(function (error) { if (error.response) { console.log(error.response.data); } });
    //setRerender(rerender*-1);
    
    console.log(rer)
  }

  return (
    <div className="Home">

      <div className="Home-header">
        <label className='username'>{user.username}</label>
        <button className="Button2" onClick={signout}>Sign out</button>
      </div>
      <br></br><br></br>
      <div className="addTask">
        <form method="post" onSubmit={addTask}>
          <label className="taskname">
            Add task
          </label><br></br>
          <input className="input taskname" name="taskname" />
          <button>Add</button>
        </form><br></br>

        <div>
          <h3>Task List:</h3>
          <ul>
            {tasks.map((item) => (
              <li key={item._id}>
              <Task task={item}/>
              </li>
            ))}
          </ul>
        </div>
        
        



      </div>







    </div>


  )
};

export default Home;
