import { useState } from 'react';
import './Login.css';


function Login() {
  const [Email, setEmail] = useState('');

  function handleSubmit(event) {
    event.preventDefault();
        if (Checker() === 1) {
            const formData = new FormData(event.target);
            const User = { "username": formData.get("username"),"email": formData.get("email"), "password": formData.get("password") }
            axios.post(`http://localhost:8000/postemail`, User)
            .then(res => {
              console.log(res);
                if(res.data.code===11000)
                { setError(11000)
                console.log("Could not create account. Email already registered");}
            })
            .catch(function (error) {
                if (error.response) {
                    console.log(error.response.data);
                }
            });            
        }
  }

  return (
    <div className="login">
      <header className="pageheader">
        <h4>Log into an Account</h4>
      </header>
      <div>
        <form method="post" onSubmit={handleSubmit}>
          <label>
            <input name="username" id="username" placeholder='USERNAME' />
            <br></br>
          </label>
          <br></br>
          <label>
            <input name="email" placeholder='EMAIL' />
            <br></br>
          </label>
          <br></br>
          <label>
            <input name="password" type='password' placeholder='PASSWORD' />
            <br></br>
          </label>
          <br></br>
          <button>Login</button>
          <a href="Signup">Don't have an account?</a>

        </form>
      </div>
    </div>
  );
};

export default Login;
