import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from "axios";
import './Home.css';
import Profile from './Profile';
import Task from './Task.js';

function Home() {
  const [user, setUser] = useState({ id: Profile.getId(), username: Profile.getUsername() });
  const [tasks, setTasks] = useState([]);
  const navigate = useNavigate();

  // console.log(user.id);
  // console.log(user.username);
  // console.log(tasks);


  useEffect(() => {
    var idcheck = Profile.getId();
    if (idcheck === 'null' || idcheck === null) {
      Profile.clearProfile();
      navigate("/Login");
    }
    else {
      const fetchTasks = async () => {
        try {
          const result = await Profile.getTasks();
          setTasks(result);
        } catch (error) { console.error('Error fetching task list:', error); }
      };
      fetchTasks();
    }
  }, []);


  function signout() {
    Profile.clearProfile();
    navigate("/Login");
  }

  function addTask(event) {
    event.preventDefault();
    const formData = new FormData(event.target);
    if (formData.get("taskname") === '') { return; }

    const task = { taskname: formData.get("taskname"), completion: false }
    var bod = { "id": user.id, "task": task };

    axios.post(`http://localhost:8000/addtask`, bod)
      .then(res => {
        console.log(res.data);
      })
      .catch(function (error) { if (error.response) { console.log(error.response.data); } });
    window.location.reload()
  }

  return (
    <div className="Home">

      <div className="Home-header">

        <div className="addTask">
          <form method="post" onSubmit={addTask} enctype="multipart/form-data">
            <label className="taskname">
              Add a new task
            </label>{' '}
            <input name="taskname" />{' '}
            <button className="Button3" type='button'><label htmlFor="file-upload">Upload Task Files</label></button>
            <input id='file-upload' type="file" multiple></input>{" "}
            <button className="Button2">Add</button>
          </form>
        </div>

        <label className='username'>{user.username}</label>
        <div className='Buttonbox2'><button className="Button2" onClick={signout}>Sign out</button></div>
      </div>
      <br></br><br></br>



      <div className='tasklist'>
        <h2>Task List</h2>
        <hr></hr>
        <ul>
          {tasks.map((item) => (
            <li key={item._id}>
              <Task task={item} />
              <hr></hr>
            </li>
          ))}
        </ul>
      </div>

    </div>


  )
};

export default Home;
