import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from "axios";
import './Home.css';
import Profile from './Profile';
import Task from './Task.js';

function Home() {
  const [user, setUser] = useState({ id: Profile.getId(), username: Profile.getUsername() });
  const [tasks, setTasks] = useState(Profile.getTasks());
  const navigate = useNavigate();
  console.log(user.id);
  console.log(user.username);
  //var tasks = [];
  var tasklist = [];
  

  useEffect(() => {
    var idcheck = Profile.getId();
    if (idcheck === 'null' || idcheck === null) {
      Profile.clearProfile();
      navigate("/Login");
    }
  }, []);

  function signout() {
    Profile.clearProfile();
    navigate("/Login");
  }

  function addTask(event) {
    event.preventDefault();
    const formData = new FormData(event.target);
    console.log(formData);
    const task = { taskname: formData.get("taskname"), completion: false }
    var bod = { "id": user.id, "task": task };

    axios.post(`http://localhost:8000/addtask`, bod)
      .then(res => {
        console.log(res.data);
      })
      .catch(function (error) { if (error.response) { console.log(error.response.data); } });
  }

  function deleteTask(event) {
    event.preventDefault();
    const formData = new FormData(event.target);
    console.log(formData);
    const task = { taskname: formData.get("taskname"), completion: false }
    var bod = { "id": user.id, "task": task };

    axios.post(`http://localhost:8000/deltask`, bod)
      .then(res => {
        console.log(res.data);
      })
      .catch(function (error) { if (error.response) { console.log(error.response.data); } });
  }

  tasks=Profile.getTask();
  console.log(tasks);

  return (
    <div className="Home">

      <div className="Home-header">
        <label className='username'>{user.username}</label>
        <button className="Button2" onClick={signout}>Sign out</button>
      </div>
      <br></br><br></br>
      <div className="addTask">
        <form method="post" onSubmit={addTask}>
          <label className="taskname">
            Add task
          </label><br></br>
          <input className="input taskname" name="taskname" />
          <button>Add</button>
        </form><br></br>

        <form method="post" onSubmit={deleteTask}>
          <label className="taskname">
            Delete task
          </label><br></br>
          <input className="input taskname" name="taskname" />
          <button>Delete</button>
        </form><br></br>

        <button >Display</button><br></br>
        

      </div>







    </div>


  )
};

export default Home;
