import './Signup.css'
import { useState } from 'react';

function Signup() {
    const [Email, setEmail] = useState('');
    const [Pword, setPword] = useState('');

    function handleSubmit(event) {
        event.preventDefault();
        if (Checker() === 1) {
            const formData = new FormData(event.target);
            setEmail(formData.get("email"));
            setPword(formData.get("password"));
            const User={"email":formData.get("email"),"password":formData.get("password")}
            axios.post(`https://jsonplaceholder.typicode.com/users`, {User}).then(res => {
            console.log(res);
            console.log(res.data);
      })
        }
    }

    function Checker() {
        var a = document.getElementById("pword1").value;
        var b = document.getElementById("pword2").value;
        var c = document.getElementById("email").value;
        var re = /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;;
        if (!(a === b)) {
            window.alert("Passwords do not match.");
            return 0;
        }
        if (!(re.test(c))) {
            window.alert("Invalid Email.");
            return 0;
        }
        return 1;
    }


    return (
        <div className="signup">
            <header className="pageheader">
                <h4>Create an Account</h4>
            </header>
            <div>
                <form method="post" onSubmit={handleSubmit}>
                    <label>
                        <input name="email" id="email" placeholder='EMAIL' />
                        <br></br>
                    </label>
                    <br></br>
                    <label>
                        <input name="password" id="pword1" type='password' placeholder='PASSWORD' />
                        <br></br>
                    </label>
                    <br></br>
                    <label>
                        <input name="repassword" id="pword2" type='password' placeholder='CONFIRM PASSWORD' />
                    </label>
                    <br></br>
                    <br></br>
                    <button>Create</button>
                </form>
            </div>
        </div>


    );
};

export default Signup;
